mapper.select:
select
  id, order_timeout, seckill_timeout, take_timeout, service_timeout, comment_timeout
from tb_order_config


        and id = #{id}


        and order_timeout = #{orderTimeout}


        and seckill_timeout = #{seckillTimeout}


        and take_timeout = #{takeTimeout}


        and service_timeout = #{serviceTimeout}


        and comment_timeout = #{commentTimeout}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_order_config


        and id = #{id}


        and order_timeout = #{orderTimeout}


        and seckill_timeout = #{seckillTimeout}


        and take_timeout = #{takeTimeout}


        and service_timeout = #{serviceTimeout}


        and comment_timeout = #{commentTimeout}
