mapper.select:
select
  id, category_id, spu_id, sku_id, order_id, order_item_id, return_order_id, title, price, num, money, pay_money, image, weight
from tb_return_order_item


        and id = #{id}


        and category_id = #{categoryId}


        and spu_id = #{spuId}


        and sku_id = #{skuId}


        and order_id = #{orderId}


        and order_item_id = #{orderItemId}


        and return_order_id = #{returnOrderId}


        and title = #{title}


        and price = #{price}


        and num = #{num}


        and money = #{money}


        and pay_money = #{payMoney}


        and image = #{image}


        and weight = #{weight}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_return_order_item


        and id = #{id}


        and category_id = #{categoryId}


        and spu_id = #{spuId}


        and sku_id = #{skuId}


        and order_id = #{orderId}


        and order_item_id = #{orderItemId}


        and return_order_id = #{returnOrderId}


        and title = #{title}


        and price = #{price}


        and num = #{num}


        and money = #{money}


        and pay_money = #{payMoney}


        and image = #{image}


        and weight = #{weight}
