mapper.select:
select
  id, category_id1, category_id2, category_id3, spu_id, sku_id, order_id, name, price, num, money, pay_money, image, weight, post_fee, is_return
from tb_order_item


        and id = #{id}


        and category_id1 = #{categoryId1}


        and category_id2 = #{categoryId2}


        and category_id3 = #{categoryId3}


        and spu_id = #{spuId}


        and sku_id = #{skuId}


        and order_id = #{orderId}


        and name = #{name}


        and price = #{price}


        and num = #{num}


        and money = #{money}


        and pay_money = #{payMoney}


        and image = #{image}


        and weight = #{weight}


        and post_fee = #{postFee}


        and is_return = #{isReturn}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_order_item


        and id = #{id}


        and category_id1 = #{categoryId1}


        and category_id2 = #{categoryId2}


        and category_id3 = #{categoryId3}


        and spu_id = #{spuId}


        and sku_id = #{skuId}


        and order_id = #{orderId}


        and name = #{name}


        and price = #{price}


        and num = #{num}


        and money = #{money}


        and pay_money = #{payMoney}


        and image = #{image}


        and weight = #{weight}


        and post_fee = #{postFee}


        and is_return = #{isReturn}
