mapper.select:
select
  id, operater, operate_time, order_id, order_status, pay_status, consign_status, remarks, money, username
from tb_order_log


        and id = #{id}


        and operater = #{operater}


        and operate_time = #{operateTime}


        and order_id = #{orderId}


        and order_status = #{orderStatus}


        and pay_status = #{payStatus}


        and consign_status = #{consignStatus}


        and remarks = #{remarks}


        and money = #{money}


        and username = #{username}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_order_log


        and id = #{id}


        and operater = #{operater}


        and operate_time = #{operateTime}


        and order_id = #{orderId}


        and order_status = #{orderStatus}


        and pay_status = #{payStatus}


        and consign_status = #{consignStatus}


        and remarks = #{remarks}


        and money = #{money}


        and username = #{username}
