select
    id, spu_id, sku_id, name, small_pic, price, cost_price, create_time, check_time, status, start_time, end_time, num, stock_count, introduction
from tb_seckill_goods
where stock_count > 0 and status = #{status} and start_time >= #{startTime} and end_time < #{endTime}

    and id not in

        #{id}

select
  id, spu_id, sku_id, name, small_pic, price, cost_price, create_time, check_time, status, start_time, end_time, num, stock_count, introduction
from tb_seckill_goods


        and id = #{seckillGoods.id}


        and spu_id = #{seckillGoods.spuId}


        and sku_id = #{seckillGoods.skuId}


        and name = #{seckillGoods.name}


        and small_pic = #{seckillGoods.smallPic}


        and price = #{seckillGoods.price}


        and cost_price = #{seckillGoods.costPrice}


        and create_time = #{seckillGoods.createTime}


        and check_time = #{seckillGoods.checkTime}


        and status = #{seckillGoods.status}


        and start_time = #{seckillGoods.startTime}


        and end_time = #{seckillGoods.endTime}


        and num = #{seckillGoods.num}


        and stock_count = #{seckillGoods.stockCount}


        and introduction = #{seckillGoods.introduction}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_seckill_goods


        and id = #{id}


        and spu_id = #{spuId}


        and sku_id = #{skuId}


        and name = #{name}


        and small_pic = #{smallPic}


        and price = #{price}


        and cost_price = #{costPrice}


        and create_time = #{createTime}


        and check_time = #{checkTime}


        and status = #{status}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and num = #{num}


        and stock_count = #{stockCount}


        and introduction = #{introduction}
