mapper.select:
select
  id, username, provinceid, cityid, areaid, phone, address, contact, is_default, alias
from tb_address


        and id = #{id}


        and username = #{username}


        and provinceid = #{provinceid}


        and cityid = #{cityid}


        and areaid = #{areaid}


        and phone = #{phone}


        and address = #{address}


        and contact = #{contact}


        and is_default = #{isDefault}


        and alias = #{alias}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_address


        and id = #{id}


        and username = #{username}


        and provinceid = #{provinceid}


        and cityid = #{cityid}


        and areaid = #{areaid}


        and phone = #{phone}


        and address = #{address}


        and contact = #{contact}


        and is_default = #{isDefault}


        and alias = #{alias}
