mapper.select:
select
  id, branch_id, xid, rollback_info, log_status, log_created, log_modified, ext
from undo_log


        and id = #{id}


        and branch_id = #{branchId}


        and xid = #{xid}


        and rollback_info = #{rollbackInfo}


        and log_status = #{logStatus}


        and log_created = #{logCreated}


        and log_modified = #{logModified}


        and ext = #{ext}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from undo_log


        and id = #{id}


        and branch_id = #{branchId}


        and xid = #{xid}


        and rollback_info = #{rollbackInfo}
    entity

        and log_status = #{logStatus}


        and log_created = #{logCreated}


        and log_modified = #{logModified}


        and ext = #{ext}
