mapper.select:
select
id, sn, name, caption, brand_id, category1_id, category2_id, category3_id, template_id, freight_id, image,
images, sale_service, introduction, spec_items, para_items, sale_num, comment_num, is_marketable,
is_enable_spec, is_delete, status
from tb_spu


        and id = #{id}


        and sn = #{sn}


        and name = #{name}


        and caption = #{caption}


        and brand_id = #{brandId}


        and category1_id = #{category1Id}


        and category2_id = #{category2Id}


        and category3_id = #{category3Id}


        and template_id = #{templateId}


        and freight_id = #{freightId}


        and image = #{image}


        and images = #{images}


        and sale_service = #{saleService}


        and introduction = #{introduction}


        and spec_items = #{specItems}


        and para_items = #{paraItems}


        and sale_num = #{saleNum}


        and comment_num = #{commentNum}


        and is_marketable = #{isMarketable}


        and is_enable_spec = #{isEnableSpec}


        and is_delete = #{isDelete}


        and status = #{status}

update tb_spu set is_marketable = #{isMarketable}
where is_marketable = 0 AND status = 1 AND is_delete = 0
AND id IN

    #{id}
